import {getConnection} from './../database/database'

const getLanguajes= async (req, res) =>{
    try {
        const connection = await getConnection()
        const result = await connection.query('Select * from tb_autor')
        console.log(result[0])
        res.json(result[0])
    } catch (error) {
        res.status(500)
        res.send(error.message)
    } 
}

const addLanguajes = async (req, res) =>{
    try {
        const { nom_autor, ape_autor, hijos_autor}=req.body
        if(nom_autor===undefined || ape_autor===undefined || hijos_autor===undefined){
            res.status(400).json({message: "Bad Request"})
        }
        const languaje ={
            nom_autor, ape_autor, hijos_autor
        }
        const connection = await getConnection()
        const result = await connection.query("INSERT INTO tb_autor SET ?", languaje)
        res.json({message: "Usuario añadido"})
    } catch (error) {
        res.status(500)
        res.send(error.message)
    } 
}

const getLanguaje= async (req, res) =>{
    try {
        const {id} = req.params;
        const connection = await getConnection()
        const result = await connection.query('Select * from tb_autor where cod_autor=?',id)
        console.log(result[0])
        res.json(result[0])
    } catch (error) {
        res.status(500)
        res.send(error.message)
    } 
}

const deleteLanguaje= async (req, res) =>{
    try {
        const {id} = req.params;
        const connection = await getConnection()
        const result = await connection.query('delete from tb_autor where cod_autor=?',id)
        console.log(result[0])
        res.json(result[0])
    } catch (error) {
        res.status(500)
        res.send(error.message)
    } 
}

const updateLanguaje= async (req, res) =>{
    try {
        const {id} = req.params;
        const { nom_autor, ape_autor, hijos_autor}=req.body
        if(id===undefined, nom_autor===undefined || ape_autor===undefined || hijos_autor===undefined){
            res.status(400).json({message: "Bad Request"})
        }
        const languaje ={ nom_autor, ape_autor, hijos_autor}
        const connection = await getConnection()
        const result = await connection.query('update tb_autor set ? where cod_autor=?',[languaje,id])
        console.log(result[0])
        res.json(result[0])
    } catch (error) {
        res.status(500)
        res.send(error.message)
    } 
}

export const methods = {
    getLanguajes,
